mapper.select:
SELECT
busa.`user_name`, bht.`number` dealNum, bht.`contractnumber` contractNum, busa.`transaction_id`, su.`relname` userrelname, bh.`xiaoqu_name` xiaoquName, CONCAT (GROUP_CONCAT(busa.`rolenum`),",") roleName, SUM( busa.`proportion`) proportion, SUM(busa.`price`) sumprice, bht.create_time
FROM bus_achievement busa,`bus_house_transaction` bht,`sys_user` su,`bus_house` bh WHERE 1=1
AND busa.user_name=#{userNameOne}
AND busa.examine_type=1 AND bht.`id`=busa.`transaction_id` AND su.`username`=busa.`user_name` AND bh.`id`=busa.`house_id`
AND DATE(busa.create_time) > #{createTimeStart} AND DATE(busa.create_time) < #{createTimeEnd}
AND DATE(busa.create_time) > #{createTimeStart}
AND DATE(busa.create_time) < #{createTimeEnd}
AND su.`store_id`=(SELECT store_id FROM sys_user WHERE username=#{userName})
GROUP BY busa.transaction_id
,busa.`user_name`
ORDER BY busa.create_time DESC

mapper.update:
UPDATE bus_achievement
SET examine_type = #{examineType}
WHERE 
	transaction_id=#{transactionId}

mapper.select:
SELECT
busa.`user_name`, bht.`number` dealNum, bht.`contractnumber` contractNum, busa.`transaction_id`, su.`relname` userrelname, bh.`xiaoqu_name` xiaoquName, CONCAT (GROUP_CONCAT(busa.`rolenum`),",") roleName, SUM( busa.`proportion`) proportion, SUM(busa.`price`) sumprice, bht.create_time, bs.store_name storeName
FROM bus_achievement busa,`bus_house_transaction` bht,`sys_user` su,`bus_house` bh,`base_store` bs
WHERE 1=1
AND busa.examine_type=1 AND bht.`id`=busa.`transaction_id` AND su.`username`=busa.`user_name` AND bh.`id`=busa.`house_id`
AND DATE(busa.create_time) > #{createTimeStart} AND DATE(busa.create_time) < #{createTimeEnd}
AND DATE(busa.create_time) > #{createTimeStart}
AND DATE(busa.create_time) < #{createTimeEnd}
AND su.`store_id`=#{storeId}
AND bs.id=su.`store_id` GROUP BY busa.transaction_id,busa.`user_name`
ORDER BY busa.create_time DESC

SELECT bs.id,bs.store_name storeName,SUM(busa.price) price FROM `base_store` bs LEFT JOIN sys_user su ON bs.id=su.`store_id` LEFT JOIN bus_achievement busa ON su.`username`=busa.`user_name` AND busa.examine_type=1 WHERE 1=1
AND DATE(busa.create_time) > #{createTimeStart} AND DATE(busa.create_time) < #{createTimeEnd}
AND DATE(busa.create_time) > #{createTimeStart}
AND DATE(busa.create_time) < #{createTimeEnd}
GROUP BY bs.id ORDER BY price DESC
