mapper.insert:
INSERT INTO bus_examine ( house_id, guest_id, transaction_id, type, shiimg, tingimg, weiimg, chuimg, huxingimg, otherimg, user_name, state, create_time , user_rel_name ) VALUES(
#{houseId}, #{guestId}, #{transactionId}, #{type}, #{shiimg}, #{tingimg}, #{weiimg}, #{chuimg}, #{huxingimg}, #{otherimg}, #{userName}, 0, NOW(), (select su.relname from sys_user su where su.username=#{userName})
)

mapper.select:
  	SELECT be.*,bh.number,bh.chaoxiang,bh.huxingshi,bh.huxingting,bh.huxingchu,bh.huxingwei
	FROM `bus_examine` be ,`sys_user` su,`sys_user_role` sur,`bus_house` bh
	WHERE 
		be.`user_name`=su.`username` 
		AND su.id=sur.user_id
		AND be.house_id=bh.id
		AND su.`store_id`=(SELECT store_id FROM sys_user WHERE username=#{username})
		
		
			AND be.type = '4'
		
		
			AND be.type != '4'
		
		
			AND be.state=#{state}	
		
		
			AND be.type=#{type}	
		
		
			AND be.result=#{result}	
		

	    
			AND DATE(be.create_time) > #{createTimeStart}	AND　DATE(be.create_time) < #{createTimeEnd}
		
		
			AND DATE(be.examine_time) > #{examineTimeStart}	AND　DATE(be.examine_time) < #{examineTimeEnd}
				

		ORDER BY be.state ,be.`create_time`DESC
