mapper.select:
SELECT `id`, `number`, `guestname`, `sex`, `guestgrade`, `type`, `heartprice`, `remarks`, `areas`, `position`, `purpose`, `label`, `iskey`, `record_user_name`, `create_user_name`, `create_time`, `collaboratorids`, `record_time`, `huxingshi`, `huxingting`, `huxingwei`, `huxingchu`, `isshare`, record_rel_name, create_rel_name FROM `vanke`.`bus_guest`
1=1
and record_user_name=#{recordUserName}
and isshare=#{isShare}
and guestname like "%"#{guestname}"%"
and sex=#{sex}
and guestgrade=#{guestgrade}
and type=#{type}
and collaboratorids like "%"#{recordUserName}"%"
