mapper.select:
		SELECT
		 
		 
		  
		FROM bus_house bh,base_store_range  bsr,sys_user su
		
		
		
		   1=1
			
				and bh.price < #{priceDown}+0 
			
			
			
				and bh.price > #{priceUp}+0 
			
			
			
				and bh.price >= #{priceUp}+0 and bh.price < #{priceDown}+0
			
			
			
				
					and CONVERT(bh.`floor`,SIGNED) <= (CONVERT(bh.`maxfloor`,SIGNED)/3)
				
				
				
					and CONVERT(bh.`floor`,SIGNED) > (CONVERT(bh.`maxfloor`,SIGNED)/3) and CONVERT(bh.`floor`,SIGNED) <= (CONVERT(bh.`maxfloor`,SIGNED)/2)+(CONVERT(bh.`maxfloor`,SIGNED)/10)
				
				
				
					and CONVERT(bh.`floor`,SIGNED) >  (CONVERT(bh.`maxfloor`,SIGNED)/2)+(CONVERT(bh.`maxfloor`,SIGNED)/10)
				
			
			
			
				 and bh.searchtext like "%"#{searchText}"%"
			
			
			
				
					  and CONVERT(bh.huxingshi,SIGNED) > 5 
				
				
					and bh.huxingshi = #{huxingType}
				
				 
			
			
			
				 and bh.huxingting = #{huxingting}
			
			
				 and bh.huxingwei = #{huxingwei}
			
			
				 and bh.huxingchu = #{huxingchu}
			
			
			
				and bh.areas < #{areasDown}+0 
			
			
			
				and bh.areas > #{areasUp}+0 
			
			
			
				and bh.areas >= #{areasUp}+0 and bh.areas < #{areasDown}+0
			
			
			
				 and bh.chaoxiang = #{chaoxiang}
			
			
			
				 and bh.region_code = #{regionCode}
			
			
				 and bh.street_id = #{streetId}
			
			
				and  xiaoqu_name like "%"#{xiaoquName}"%"
			
			
				and  number like "%"#{number}"%"
			
		
			
			
				 and bh.state = #{state}
			
			
				 and bh.isspecial = #{isspecial}
			
			
				 and bh.type = #{type}
			
			
			     and bh.isshare = 0
			
			
				 and  su.username=#{userName}	
				 and  bsr.store_id=su.store_id					
				 and  bh.region_name=bsr.region_name
				 and  bh.street_name=bsr.street_name
				 and bh.isshare = 1
			
			
			
				 and bh.grade = #{grade}
			
			
				 and bh.iskey = #{iskey}
			
			
			
					and  su.username=#{userName}	
					and  bsr.store_id=su.store_id					
					and  bh.region_name=bsr.region_name
					and  bh.street_name=bsr.street_name
				
			
			
					and record_user_name=#{recordUserName}
			
	
		
		group by  bh.id
		order by  bh.create_time desc

mapper.insert:
INSERT INTO bus_house ( `owner`, `phone`, `price`, `floor`, maxfloor, numfloor, numunit, numhousehold, huxingshi, huxingting, huxingwei, huxingchu, `areas`, hiddenarea, `chaoxiang`, `grade`, `record_user_name`, `create_user_name`, `create_time`, `introduce`, `type`, `state`, region_code, street_id, xiaoqu_name, region_name, street_name, `address`, `iskey`, record_time, isfine, isshare, isspecial, searchtext, record_rel_name, create_rel_name, `owner1`, `phone1`, layout
,key_rel_name ,key_user_name ,key_store_id ,key_number
) VALUES ( #{owner}, #{phone}, #{price}, #{floor}, #{maxfloor}, #{numfloor}, #{numunit}, #{numhousehold}, #{huxingshi}, #{huxingting}, #{huxingwei}, #{huxingchu}, #{areas}, #{hiddenarea}, #{chaoxiang}, #{grade}, #{recordUserName}, #{createUserName}, NOW(), #{introduce}, #{type}, 0, #{regionCode}, #{streetId}, #{xiaoquName}, (select region_name from base_region where region_code=#{regionCode}), (select street_name from base_street where id=#{streetId}), #{address}, #{iskey}, NOW(), 0, 0, 0, CONCAT_WS('','大连市',(select region_name from base_region where region_code=#{regionCode}),(select street_name from base_street where id=#{streetId}),#{xiaoquName},#{address},#{numfloor},#{numunit},#{numhousehold}), (SELECT relname FROM sys_user WHERE username=#{recordUserName}), (SELECT relname FROM sys_user WHERE username=#{recordUserName}), #{owner1}, #{phone1}, #{layout}
,(SELECT relname FROM sys_user WHERE username=#{recordUserName}) ,#{recordUserName}, #{keyStoreId}, #{keyNumber}
);

mapper.select:
SELECT * 
FROM bus_house
WHERE
				region_code= #{regionCode}
				and street_id=#{streetId}
				and xiaoqu_name=#{xiaoquName}
				and numfloor=#{numfloor}
				and numunit=#{numunit}
				and numhousehold=#{numhousehold}
				and floor=#{floor}
	AND state <>'1'
